<script>
export default {
  name: "CardComponent",
  props: {
    card: Object,
  },
  methods: {
    srcFixer: function (src) {
      return require(`@/assets/${src}`);
    },
  },
  computed: {
    sale: function () {
      let sale = String;
      if (
        this.card.price.old &&
        this.card.price.old != 0 &&
        this.card.price.old > this.card.price.new
      ) {
        sale =
          Math.round(100 - (this.card.price.new / this.card.price.old) * 100) +
          "%";
      } else {
        sale = false;
      }
      return sale;
    },
  },
};
</script>

